import React, { useState, useEffect } from 'react';
import { Search, Volume2, Star, BookOpen, ArrowRight, Loader2, Layers, X, ChevronDown, ChevronUp, Info, Mic, List, Grid } from 'lucide-react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInAnonymously, onAuthStateChanged } from 'firebase/auth';
import { getFirestore, collection, addDoc, query, onSnapshot, deleteDoc, doc, serverTimestamp } from 'firebase/firestore';

// ==========================================
// ⚠️ 请在下方填入您的配置信息
// ==========================================

const firebaseConfig = {
  apiKey: "AIzaSyCBItd52zWdktiEuHmQIXbB4Q7sLM5602o",
  authDomain: "frenchapp-6b44a.firebaseapp.com",
  projectId: "frenchapp-6b44a",
  storageBucket: "frenchapp-6b44a.firebasestorage.app",
  messagingSenderId: "988616724582",
  appId: "1:988616724582:web:723f9d9c75f8810b019da7"
};


const GEMINI_API_KEY = "AIzaSyBivevjn0wQ3dD2OCuh98L2iALBssZfsq4"; 

// ==========================================

const app = initializeApp(firebaseConfig);
const auth = getAuth(app);
const db = getFirestore(app);
const appId = "french-memo-app";

// --- API Helper for Gemini ---

// 1. 获取单个单词详情
const fetchWordData = async (word) => {
  const apiKey = GEMINI_API_KEY; 
  if (!apiKey || apiKey.includes("YOUR_GEMINI_API_KEY")) throw new Error("API Key not set");

  const prompt = `
    Analyze the French word "${word}". 
    Return strictly valid JSON (no markdown) with this schema:
    {
      "word": "${word}",
      "definitions": {
        "en": "Concise English meaning (max 5 words)",
        "zh": "简洁中文释义"
      },
      "partOfSpeech": "abbrev. (e.g. n.m., adj.)",
      "collection": "Category Name (e.g. Fruits, Voyage)",
      "relatedWords": ["synonym1", "antonym1", "related1"],
      "relatedPhrases": [
        {"fr": "Phrase 1", "zh": "Meaning 1"},
        {"fr": "Phrase 2", "zh": "Meaning 2"},
        {"fr": "Phrase 3", "zh": "Meaning 3"},
        {"fr": "Phrase 4", "zh": "Meaning 4"}
      ],
      "examplePhrase": {
        "fr": "Simple French sentence",
        "en": "English translation",
        "zh": "Chinese translation",
        "explanation": "Brief grammar/cultural note"
      }
    }
  `;

  const response = await fetch(
    `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-09-2025:generateContent?key=${apiKey}`,
    {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ contents: [{ parts: [{ text: prompt }] }] }),
    }
  );
  const data = await response.json();
  const text = data.candidates?.[0]?.content?.parts?.[0]?.text;
  return text ? JSON.parse(text) : null;
};

// 2. 获取专辑列表详情 (新功能)
const fetchAlbumData = async (category) => {
  const apiKey = GEMINI_API_KEY;
  const prompt = `
    Generate a vocabulary list for the French category: "${category}".
    Return strictly valid JSON (no markdown) containing a list of 10-12 items.
    Schema:
    {
      "category": "${category}",
      "items": [
        {
          "word": "French word",
          "article": "le/la/un/une/l'" (if noun, else empty string),
          "zh": "Chinese meaning",
          "isVisual": boolean (true if it's a concrete object like apple/car, false if abstract like happy/think)
        }
      ]
    }
  `;

  const response = await fetch(
    `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-09-2025:generateContent?key=${apiKey}`,
    {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ contents: [{ parts: [{ text: prompt }] }] }),
    }
  );
  const data = await response.json();
  const text = data.candidates?.[0]?.content?.parts?.[0]?.text;
  return text ? JSON.parse(text) : null;
};

export default function App() {
  const [user, setUser] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(false);
  const [currentWordData, setCurrentWordData] = useState(null);
  const [albumData, setAlbumData] = useState(null); // 新增：专辑数据状态
  const [favorites, setFavorites] = useState([]);
  const [activeTab, setActiveTab] = useState('search'); // 'search', 'favorites', 'album'

  const getImageUrl = (keyword, size = 'medium') => {
    const dimensions = size === 'small' ? 'width=200&height=200' : 'width=600&height=400';
    return `https://image.pollinations.ai/prompt/realistic%20${keyword}%20french%20context?${dimensions}&nologo=true`;
  };

  useEffect(() => {
    signInAnonymously(auth).catch(console.error);
    const unsubscribe = onAuthStateChanged(auth, setUser);
    return () => unsubscribe();
  }, []);

  useEffect(() => {
    if (!user) return;
    const q = query(collection(db, 'artifacts', appId, 'users', user.uid, 'favorites'));
    const unsubscribe = onSnapshot(q, (snapshot) => {
        const favs = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        favs.sort((a, b) => (b.timestamp?.seconds || 0) - (a.timestamp?.seconds || 0));
        setFavorites(favs);
      });
    return () => unsubscribe();
  }, [user]);

  // --- Handlers ---
  const handleSearch = async (e, wordOverride = null) => {
    if (e) e.preventDefault();
    const term = wordOverride || searchTerm;
    if (!term.trim()) return;

    setLoading(true);
    setCurrentWordData(null);
    setAlbumData(null);
    setActiveTab('search');

    try {
      const data = await fetchWordData(term);
      setCurrentWordData(data);
    } catch (err) { alert("Error fetching word."); } 
    finally { setLoading(false); }
  };

  const handleSpeak = (text) => {
    if ('speechSynthesis' in window) {
      window.speechSynthesis.cancel();
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.lang = 'fr-FR';
      utterance.rate = 0.9;
      window.speechSynthesis.speak(utterance);
    }
  };

  const handleAlbumClick = async (category) => {
    setLoading(true);
    setAlbumData(null);
    setActiveTab('album'); // 切换到专辑视图
    try {
      const data = await fetchAlbumData(category);
      setAlbumData(data);
    } catch (err) { console.error(err); alert("Failed to load album."); setActiveTab('search'); }
    finally { setLoading(false); }
  };

  const addToFavorites = async () => {
    if (!user || !currentWordData) return;
    await addDoc(collection(db, 'artifacts', appId, 'users', user.uid, 'favorites'), { ...currentWordData, timestamp: serverTimestamp() });
  };

  const removeFromFavorites = async (id) => {
    await deleteDoc(doc(db, 'artifacts', appId, 'users', user.uid, 'favorites', id));
  };

  // --- Components ---

  const AlbumView = ({ data }) => {
    if (!data) return null;
    return (
      <div className="space-y-6 animate-in fade-in slide-in-from-bottom-4 duration-500">
        <div className="flex items-center gap-3 mb-6">
          <button onClick={() => setActiveTab('search')} className="p-2 hover:bg-gray-100 rounded-full transition-colors">
            <ArrowRight className="rotate-180 text-gray-500" />
          </button>
          <h2 className="text-2xl font-bold text-gray-900 flex items-center gap-2">
            <Layers className="text-blue-600" size={24} />
            {data.category}
          </h2>
        </div>

        {/* 混合布局：根据 isVisual 属性决定显示方式 */}
        <div className="grid grid-cols-2 gap-4">
          {data.items.map((item, idx) => {
            if (item.isVisual) {
              // 视觉类单词：图片 + 定冠词 + 单词
              return (
                <button 
                  key={idx}
                  onClick={() => handleSearch(null, item.word)}
                  className="group relative flex flex-col items-center bg-white rounded-2xl overflow-hidden shadow-sm hover:shadow-lg transition-all border border-gray-100 text-left"
                >
                  <div className="w-full aspect-[4/3] bg-gray-50 relative overflow-hidden">
                    <img 
                      src={getImageUrl(item.word, 'small')} 
                      alt={item.word} 
                      className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-700"
                    />
                    <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/60 to-transparent p-3 pt-8">
                       <p className="text-white font-medium text-lg">
                         <span className="opacity-70 text-sm mr-1 font-normal">{item.article}</span>
                         {item.word}
                       </p>
                    </div>
                  </div>
                </button>
              );
            } else {
              // 抽象类单词：宽卡片，仅文字
              return (
                <button 
                  key={idx}
                  onClick={() => handleSearch(null, item.word)}
                  className="col-span-2 flex items-center justify-between p-4 bg-white rounded-xl shadow-sm hover:shadow-md border border-gray-100 hover:border-blue-200 transition-all group"
                >
                  <div>
                    <span className="text-lg font-bold text-gray-800 group-hover:text-blue-600 transition-colors">{item.word}</span>
                  </div>
                  <span className="text-sm text-gray-500 font-medium">{item.zh}</span>
                </button>
              );
            }
          })}
        </div>
      </div>
    );
  };

  const WordCard = ({ data, isFavoriteView = false, onDelete }) => {
    const [showPhraseDetails, setShowPhraseDetails] = useState(false);
    const [showAllPhrases, setShowAllPhrases] = useState(false);

    const togglePhrase = (e) => {
      e.stopPropagation();
      handleSpeak(data.examplePhrase.fr);
      setShowPhraseDetails(!showPhraseDetails);
    };

    return (
    <div className="bg-white rounded-[2rem] shadow-sm hover:shadow-xl transition-all duration-300 overflow-hidden border border-gray-100 mb-8">
      {/* 图片 */}
      <div className="h-56 overflow-hidden bg-gray-50 relative">
        <img 
          src={getImageUrl(data.word)} 
          alt={data.word}
          className="w-full h-full object-cover"
        />
      </div>

      {/* 优化后的标签栏：字体调小，支持点击专辑 */}
      <div className="flex gap-2 px-8 pt-4 bg-white">
        <span className="bg-blue-50 px-3 py-1.5 rounded-full text-xs font-bold text-blue-600 uppercase tracking-wider">
          {data.partOfSpeech}
        </span>
        <button 
          onClick={() => handleAlbumClick(data.collection)}
          className="bg-gray-100 hover:bg-gray-200 px-3 py-1.5 rounded-full text-xs font-bold text-gray-600 flex items-center gap-1.5 transition-colors cursor-pointer"
        >
          <Layers size={14} />
          {data.collection}
        </button>
      </div>

      <div className="p-8 pt-2">
        {/* 单词标题：字体调小 text-4xl */}
        <div className="flex justify-between items-center mb-6">
          <div className="flex items-center gap-3">
            <h2 className="text-4xl font-bold text-gray-900 tracking-tight">{data.word}</h2>
            <button onClick={(e) => { e.stopPropagation(); handleSpeak(data.word); }} className="p-2.5 rounded-full bg-blue-50 text-blue-600 hover:bg-blue-100 transition-colors">
              <Volume2 size={20} />
            </button>
          </div>
          {!isFavoriteView ? (
            <button onClick={addToFavorites} className="p-2.5 rounded-full text-gray-300 hover:text-yellow-400 hover:bg-yellow-50 transition-colors">
              <Star size={22} />
            </button>
          ) : (
            <button onClick={() => onDelete(data.id)} className="p-2.5 rounded-full text-gray-300 hover:text-red-500 hover:bg-red-50 transition-colors">
              <X size={22} />
            </button>
          )}
        </div>

        {/* 释义：简洁化，去除冗余解释 */}
        <div className="mb-8 space-y-2">
          <div className="flex items-baseline gap-3">
             <span className="text-xl font-medium text-gray-900">{data.definitions.zh}</span>
             <span className="text-sm text-gray-400">|</span>
             <span className="text-base text-gray-500 font-medium">{data.definitions.en}</span>
          </div>
        </div>

        {/* 交互式例句 */}
        <div className="space-y-4">
          <div 
            onClick={togglePhrase}
            className="bg-blue-50/50 rounded-xl border border-blue-100 overflow-hidden cursor-pointer hover:bg-blue-50 transition-colors"
          >
            <div className="p-4 flex justify-between items-start gap-3">
               <div>
                 <div className="flex items-center gap-2 mb-1">
                    <span className="text-[10px] font-bold text-blue-400 uppercase tracking-widest">Exemple</span>
                    <Mic size={12} className="text-blue-400" />
                 </div>
                 <p className="text-xl text-gray-800 font-serif italic leading-snug">"{data.examplePhrase.fr}"</p>
               </div>
               <ChevronDown size={18} className={`text-blue-300 mt-2 transition-transform ${showPhraseDetails ? 'rotate-180' : ''}`} />
            </div>

            {showPhraseDetails && (
              <div className="px-4 pb-4 pt-0 space-y-2 animate-in slide-in-from-top-2">
                 <p className="text-sm text-gray-600">{data.examplePhrase.zh}</p>
                 <p className="text-sm text-gray-500">{data.examplePhrase.en}</p>
                 {data.examplePhrase.explanation && (
                   <div className="mt-2 text-xs text-blue-500 bg-blue-100/50 p-2 rounded-lg">
                     💡 {data.examplePhrase.explanation}
                   </div>
                 )}
              </div>
            )}
          </div>

          {/* 相关短语：默认展示2条 */}
          {data.relatedPhrases && data.relatedPhrases.length > 0 && (
            <div className="pl-2">
              <h4 className="text-xs font-bold text-gray-400 uppercase mb-3 tracking-wider">常用短语</h4>
              <div className="space-y-2">
                {data.relatedPhrases.slice(0, showAllPhrases ? undefined : 2).map((phrase, idx) => (
                   <div key={idx} className="flex justify-between items-center group py-1 border-b border-gray-50 last:border-0">
                      <span className="text-gray-700 font-medium cursor-pointer hover:text-blue-600" onClick={() => handleSpeak(phrase.fr)}>
                        {phrase.fr}
                      </span>
                      <span className="text-sm text-gray-400">{phrase.zh}</span>
                   </div>
                ))}
              </div>
              {data.relatedPhrases.length > 2 && (
                <button 
                  onClick={() => setShowAllPhrases(!showAllPhrases)}
                  className="text-xs text-blue-500 font-medium mt-2 hover:underline flex items-center gap-1"
                >
                  {showAllPhrases ? "收起" : `查看更多 (${data.relatedPhrases.length - 2})`}
                </button>
              )}
            </div>
          )}
        </div>

        {/* 底部相关词 - 保持图片小卡片 */}
        {!isFavoriteView && data.relatedWords && data.relatedWords.length > 0 && (
          <div className="pt-8 mt-8 border-t border-gray-100">
             <div className="flex overflow-x-auto pb-4 gap-3 no-scrollbar">
                {data.relatedWords.map((word, idx) => (
                  <button key={idx} onClick={() => handleSearch(null, word)} className="flex-shrink-0 flex flex-col items-center w-20 group">
                    <div className="w-16 h-16 rounded-xl overflow-hidden mb-2 bg-gray-100 border border-gray-200 group-hover:border-blue-300 transition-all">
                       <img src={getImageUrl(word, 'small')} className="w-full h-full object-cover opacity-80 group-hover:opacity-100 group-hover:scale-110 transition-all" />
                    </div>
                    <span className="text-xs text-gray-500 group-hover:text-blue-600 truncate w-full text-center">{word}</span>
                  </button>
                ))}
             </div>
          </div>
        )}
      </div>
    </div>
    );
  };

  return (
    <div className="min-h-screen bg-gray-50 font-sans text-gray-900 pb-20">
      {/* Header */}
      <div className="bg-white/80 backdrop-blur-md border-b border-gray-100 sticky top-0 z-20">
        <div className="max-w-xl mx-auto px-6 py-4 flex justify-between items-center">
          <h1 
            onClick={() => setActiveTab('search')}
            className="text-xl font-bold text-gray-900 flex items-center gap-2 tracking-tight cursor-pointer"
          >
            <BookOpen size={22} className="text-blue-600" />
            FrenchMemo
          </h1>
          <div className="text-xs text-gray-400 font-medium font-mono">
             {user ? `UID: ${user.uid.slice(0,4)}` : 'Connecting...'}
          </div>
        </div>
        {activeTab !== 'album' && (
          <div className="flex max-w-xl mx-auto px-6">
            <button onClick={() => setActiveTab('search')} className={`flex-1 pb-3 text-sm font-semibold border-b-2 transition-all ${activeTab === 'search' ? 'border-blue-600 text-blue-600' : 'border-transparent text-gray-400 hover:text-gray-600'}`}>Search</button>
            <button onClick={() => setActiveTab('favorites')} className={`flex-1 pb-3 text-sm font-semibold border-b-2 transition-all ${activeTab === 'favorites' ? 'border-blue-600 text-blue-600' : 'border-transparent text-gray-400 hover:text-gray-600'}`}>Favorites <span className="ml-1 px-1.5 py-0.5 bg-gray-100 text-gray-500 rounded-full text-xs">{favorites.length}</span></button>
          </div>
        )}
      </div>

      <div className="max-w-xl mx-auto px-6 py-8">
        {activeTab === 'album' && loading && (
           <div className="text-center py-20 flex flex-col items-center">
             <Loader2 className="animate-spin text-blue-600 mb-4" size={32} />
             <p className="text-gray-500">正在生成专辑单词列表...</p>
           </div>
        )}

        {activeTab === 'album' && !loading && albumData && <AlbumView data={albumData} />}

        {activeTab === 'search' && (
          <>
            <form onSubmit={handleSearch} className="relative mb-10 group z-10">
              <div className={`absolute inset-0 bg-blue-500/5 rounded-full blur-xl transition-opacity duration-300 ${loading ? 'opacity-100' : 'opacity-0 group-hover:opacity-100'}`}></div>
              <input type="text" value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} placeholder="Tapez un mot français..." className="w-full pl-14 pr-14 py-4 bg-white border-0 rounded-full shadow-[0_8px_30px_rgb(0,0,0,0.06)] text-xl font-medium placeholder:text-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500/20 transition-all relative z-10" />
              <Search className="absolute left-5 top-1/2 -translate-y-1/2 text-gray-400 z-20" size={22} />
              <button type="submit" disabled={loading} className="absolute right-3 top-1/2 -translate-y-1/2 p-2.5 bg-blue-600 text-white rounded-full hover:bg-blue-700 transition-colors disabled:opacity-70 hover:scale-105 active:scale-95 z-20 shadow-md">
                {loading ? <Loader2 className="animate-spin" size={20} /> : <ArrowRight size={20} />}
              </button>
            </form>
            {currentWordData && <WordCard data={currentWordData} />}
            {!currentWordData && !loading && (
              <div className="text-center py-16">
                <div className="bg-white p-6 rounded-full inline-block shadow-sm mb-6"><Search size={40} className="text-blue-100" /></div>
                <h3 className="text-gray-900 font-bold text-lg mb-2">Prêt à apprendre ?</h3>
                <p className="text-gray-500 mb-8">输入单词开始学习，或点击下方建议。</p>
                <div className="flex flex-wrap justify-center gap-3">
                  {['Pomme', 'Chat', 'Heureux', 'Maison', 'Voyage'].map(s => (
                    <button key={s} onClick={() => handleSearch(null, s)} className="px-5 py-2 bg-white shadow-sm hover:shadow-md border border-gray-100 rounded-full text-sm font-medium text-gray-600 hover:text-blue-600 transition-all hover:-translate-y-0.5">{s}</button>
                  ))}
                </div>
              </div>
            )}
          </>
        )}
        {activeTab === 'favorites' && (
          <div className="space-y-8">
            {favorites.length === 0 ? (
              <div className="text-center py-20"><p className="text-gray-500 font-medium">暂无收藏单词</p></div>
            ) : (
              favorites.map(fav => <WordCard key={fav.id} data={fav} isFavoriteView={true} onDelete={removeFromFavorites} />)
            )}
          </div>
        )}
      </div>
    </div>
  );
}
