import React, { useState, useEffect } from 'react';
import { Search, Volume2, Star, BookOpen, ArrowRight, Loader2, Layers, X, ChevronDown, ChevronUp, Info, Mic } from 'lucide-react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInAnonymously, onAuthStateChanged } from 'firebase/auth';
import { getFirestore, collection, addDoc, query, onSnapshot, deleteDoc, doc, serverTimestamp } from 'firebase/firestore';

// ==========================================
// âš ï¸ è¯·åœ¨ä¸‹æ–¹å¡«å…¥æ‚¨çš„é…ç½®ä¿¡æ¯
// ==========================================

// 1. å¡«å…¥æ‚¨çš„ Firebase é…ç½®ä¿¡æ¯
const firebaseConfig = {
  apiKey: "YOUR_FIREBASE_API_KEY",
  authDomain: "your-project.firebaseapp.com",
  projectId: "your-project-id",
  storageBucket: "your-project.appspot.com",
  messagingSenderId: "123456789",
  appId: "1:123456:web:abcdef"
};

// 2. å¡«å…¥æ‚¨çš„ Gemini API Key
const GEMINI_API_KEY = "YOUR_GEMINI_API_KEY_HERE"; 

// ==========================================

// åˆå§‹åŒ– Firebase
const app = initializeApp(firebaseConfig);
const auth = getAuth(app);
const db = getFirestore(app);
const appId = "french-memo-app";

// --- API Helper for Gemini ---
const fetchWordData = async (word) => {
  const apiKey = GEMINI_API_KEY; 

  if (!apiKey || apiKey.includes("YOUR_GEMINI_API_KEY")) {
    alert("è¯·åœ¨ä»£ç ä¸­è®¾ç½®æ‚¨çš„ Gemini API Key");
    throw new Error("API Key not set");
  }

  // æ›´æ–°äº† Promptï¼Œè¦æ±‚æ›´ç®€æ´çš„è‹±æ–‡å®šä¹‰ï¼Œå¹¶å¢åŠ äº†ä¾‹å¥çš„ä¸­æ–‡ç¿»è¯‘å’Œç®€ä»‹å­—æ®µ
  const prompt = `
    Analyze the French word "${word}". 
    Return strictly valid JSON (no markdown formatting) with this schema:
    {
      "word": "${word}",
      "definitions": {
        "en": "Very concise, dictionary-style English definition (max 10 words).",
        "zh": "Chinese definition (Simplified)"
      },
      "partOfSpeech": "noun/verb/adj etc (in French abbreviation if possible, e.g. n.m.)",
      "collection": "Semantic category/Album name (e.g., Fruits & Vegetables, Animals, Emotions)",
      "relatedWords": ["synonym1", "antonym1", "related_concept_word"],
      "examplePhrase": {
        "fr": "A simple, natural French sentence using the word",
        "en": "English translation of the sentence",
        "zh": "Chinese translation of the sentence",
        "explanation": "A very brief (1 sentence) note on the grammar or cultural context of this phrase."
      }
    }
  `;

  try {
    const response = await fetch(
      `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-09-2025:generateContent?key=${apiKey}`,
      {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          contents: [{ parts: [{ text: prompt }] }],
          generationConfig: { responseMimeType: "application/json" }
        }),
      }
    );

    const data = await response.json();
    const text = data.candidates?.[0]?.content?.parts?.[0]?.text;
    return text ? JSON.parse(text) : null;
  } catch (error) {
    console.error("Gemini API Error:", error);
    throw new Error("Failed to fetch word data.");
  }
};

export default function App() {
  const [user, setUser] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(false);
  const [currentWordData, setCurrentWordData] = useState(null);
  const [favorites, setFavorites] = useState([]);
  const [activeTab, setActiveTab] = useState('search');

  // ç”Ÿæˆå›¾ç‰‡ URL (ç”¨äºä¸»å›¾å’Œç›¸å…³è¯å°å›¾)
  const getImageUrl = (keyword, size = 'medium') => {
    const dimensions = size === 'small' ? 'width=200&height=200' : 'width=600&height=400';
    return `https://image.pollinations.ai/prompt/realistic%20${keyword}%20french%20context?${dimensions}&nologo=true`;
  };

  // --- Auth & Data Sync ---
  useEffect(() => {
    const initAuth = async () => {
      try { await signInAnonymously(auth); } catch (error) { console.error("Auth Error:", error); }
    };
    initAuth();
    const unsubscribe = onAuthStateChanged(auth, setUser);
    return () => unsubscribe();
  }, []);

  useEffect(() => {
    if (!user) return;
    const q = query(collection(db, 'artifacts', appId, 'users', user.uid, 'favorites'));
    const unsubscribe = onSnapshot(q, (snapshot) => {
        const favs = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        favs.sort((a, b) => (b.timestamp?.seconds || 0) - (a.timestamp?.seconds || 0));
        setFavorites(favs);
      }, (error) => console.error("Firestore sync error:", error));
    return () => unsubscribe();
  }, [user]);

  // --- Core Actions ---
  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchTerm.trim()) return;
    setLoading(true); setCurrentWordData(null); setActiveTab('search');
    try {
      const data = await fetchWordData(searchTerm);
      setCurrentWordData(data);
    } catch (err) { alert("Could not find word. Please check your API key."); } 
    finally { setLoading(false); }
  };

  const handleSpeak = (text) => {
    if ('speechSynthesis' in window) {
      // å–æ¶ˆä¹‹å‰çš„å‘éŸ³é˜Ÿåˆ—ï¼Œé¿å…é‡å 
      window.speechSynthesis.cancel();
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.lang = 'fr-FR';
      utterance.rate = 0.9;
      window.speechSynthesis.speak(utterance);
    }
  };

  const addToFavorites = async () => {
    if (!user || !currentWordData) return;
    await addDoc(collection(db, 'artifacts', appId, 'users', user.uid, 'favorites'), { ...currentWordData, timestamp: serverTimestamp() });
  };

  const removeFromFavorites = async (id) => {
    if (!user) return;
    await deleteDoc(doc(db, 'artifacts', appId, 'users', user.uid, 'favorites', id));
  };

  const searchRelated = (word) => {
    setSearchTerm(word); setLoading(true); setCurrentWordData(null);
    fetchWordData(word).then(data => { setCurrentWordData(data); setLoading(false); });
  };

  // --- UI Components ---

  const WordCard = ({ data, isFavoriteView = false, onDelete }) => {
    // æ§åˆ¶ä¾‹å¥è¯¦æƒ…æ˜¾ç¤ºçš„å†…éƒ¨çŠ¶æ€
    const [showPhraseDetails, setShowPhraseDetails] = useState(false);

    const togglePhrase = () => {
      // ç‚¹å‡»å‘éŸ³å¹¶åˆ‡æ¢æ˜¾ç¤ºçŠ¶æ€
      handleSpeak(data.examplePhrase.fr);
      setShowPhraseDetails(!showPhraseDetails);
    };

    return (
    <div className="bg-white rounded-3xl shadow-sm hover:shadow-xl transition-all duration-300 overflow-hidden border border-gray-100 mb-8">
      {/* 1. å›¾ç‰‡åŒºåŸŸ (æ— é®æŒ¡) */}
      <div className="h-56 overflow-hidden bg-gray-50">
        <img 
          src={getImageUrl(data.word)} 
          alt={data.word}
          className="w-full h-full object-cover"
          onError={(e) => e.target.style.display = 'none'} 
        />
      </div>

      {/* 1. è¯æ€§å’Œåˆ†ç±»æ ‡ç­¾æ  (ç§»åˆ°å›¾ç‰‡ä¸‹æ–¹) */}
      <div className="flex gap-3 px-8 pt-6 bg-white">
        <span className="bg-blue-50 px-3 py-1.5 rounded-full text-sm font-bold text-blue-600 uppercase tracking-wider">
          {data.partOfSpeech}
        </span>
        <span className="bg-gray-100 px-3 py-1.5 rounded-full text-sm font-bold text-gray-600 flex items-center gap-1.5">
          <Layers size={14} />
          {data.collection}
        </span>
      </div>

      <div className="p-8 pt-4">
        {/* å•è¯æ ‡é¢˜æ å’Œæ“ä½œæŒ‰é’® */}
        <div className="flex justify-between items-start mb-8">
          <div className="flex items-center gap-4">
            <h2 className="text-5xl font-bold text-gray-900 tracking-tight">{data.word}</h2>
            <button 
              onClick={(e) => { e.stopPropagation(); handleSpeak(data.word); }}
              className="p-3 rounded-full bg-blue-50 text-blue-600 hover:bg-blue-100 transition-colors"
            >
              <Volume2 size={24} />
            </button>
          </div>
          {!isFavoriteView ? (
            <button onClick={addToFavorites} className="p-3 rounded-full text-gray-400 hover:text-yellow-500 hover:bg-yellow-50 transition-colors">
              <Star size={24} />
            </button>
          ) : (
            <button onClick={() => onDelete(data.id)} className="p-3 rounded-full text-gray-300 hover:text-red-500 hover:bg-red-50 transition-colors group">
              <X size={24} className="group-hover:scale-110 transition-transform" />
            </button>
          )}
        </div>

        <div className="space-y-10">
          {/* é‡Šä¹‰éƒ¨åˆ† (2. ç®€æ´è‹±æ–‡) */}
          <div className="space-y-6">
            <div>
              <h3 className="text-xs font-semibold text-gray-400 uppercase tracking-wider mb-2 flex items-center gap-1">ğŸ‡¨ğŸ‡³ ä¸­æ–‡é‡Šä¹‰</h3>
              <p className="text-2xl text-gray-900 leading-relaxed font-bold">{data.definitions.zh}</p>
            </div>
            <div>
              <h3 className="text-xs font-semibold text-gray-400 uppercase tracking-wider mb-2 flex items-center gap-1">ğŸ‡¬ğŸ‡§ Concise English</h3>
              <p className="text-lg text-gray-700 leading-relaxed font-medium">{data.definitions.en}</p>
            </div>
          </div>

          {/* 4. äº¤äº’å¼ä¾‹å¥åŒºåŸŸ */}
          <div className="bg-blue-50/50 rounded-2xl border border-blue-100 overflow-hidden">
            <button 
              onClick={togglePhrase}
              className="w-full text-left p-5 flex items-start justify-between group hover:bg-blue-50 transition-colors"
            >
              <div className="flex gap-4">
                 <Mic size={20} className="text-blue-400 mt-1 shrink-0 group-hover:text-blue-600 transition-colors" />
                 <div>
                   <h3 className="text-xs font-semibold text-blue-400 uppercase tracking-wider mb-1">Exemple (ç‚¹å‡»å‘éŸ³å¹¶å±•å¼€)</h3>
                   <p className="text-2xl text-gray-800 font-serif leading-snug italic pr-4">
                     "{data.examplePhrase.fr}"
                   </p>
                 </div>
              </div>
              <ChevronDown size={20} className={`text-blue-300 transform transition-transform duration-300 mt-2 ${showPhraseDetails ? 'rotate-180' : ''}`} />
            </button>

            {/* å±•å¼€çš„å†…å®¹ (4. ä¸­è‹±ç¿»è¯‘å’Œç®€ä»‹) */}
            <div className={`px-5 pb-5 pt-2 border-t border-blue-100/50 space-y-3 transition-all duration-300 ${showPhraseDetails ? 'block opacity-100' : 'hidden opacity-0'}`}>
               <div className="space-y-2">
                <p className="text-gray-600 text-sm flex gap-2">
                  <span className="font-semibold text-blue-600 shrink-0">EN:</span> {data.examplePhrase.en}
                </p>
                <p className="text-gray-600 text-sm flex gap-2">
                  <span className="font-semibold text-blue-600 shrink-0">ZH:</span> {data.examplePhrase.zh}
                </p>
               </div>
               {data.examplePhrase.explanation && (
                 <div className="bg-white/60 p-3 rounded-lg text-sm text-gray-500 italic">
                   ğŸ’¡ ç®€ä»‹: {data.examplePhrase.explanation}
                 </div>
               )}
            </div>
          </div>

          {/* 3. åº•éƒ¨ä¸“è¾‘å’Œç›¸å…³è¯ (å›¾æ–‡å±•ç¤º) */}
          {!isFavoriteView && data.relatedWords && data.relatedWords.length > 0 && (
            <div className="pt-8 border-t border-gray-100">
              <div className="flex items-center gap-2 mb-6">
                <Layers size={18} className="text-blue-600" />
                <h3 className="text-lg font-bold text-gray-900">
                  {data.collection} ä¸“è¾‘ä¸ç›¸å…³è¯
                </h3>
              </div>

              <div className="grid grid-cols-3 sm:grid-cols-4 gap-4">
                {data.relatedWords.map((word, idx) => (
                  <button 
                    key={idx}
                    onClick={() => searchRelated(word)}
                    className="group flex flex-col items-center text-center"
                  >
                    <div className="w-full aspect-square rounded-2xl overflow-hidden mb-3 shadow-sm border border-gray-200 group-hover:shadow-md group-hover:border-blue-300 transition-all relative">
                       <div className="absolute inset-0 bg-black/0 group-hover:bg-black/10 transition-colors z-10"></div>
                       <img 
                         src={getImageUrl(word, 'small')} 
                         alt={word}
                         className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-500"
                       />
                    </div>
                    <span className="text-sm font-medium text-gray-700 group-hover:text-blue-600 transition-colors">
                      {word}
                    </span>
                  </button>
                ))}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
    );
  };

  return (
    <div className="min-h-screen bg-gray-50 font-sans text-gray-900 pb-20">
      {/* Header */}
      <div className="bg-white/80 backdrop-blur-md border-b border-gray-100 sticky top-0 z-20">
        <div className="max-w-xl mx-auto px-6 py-4 flex justify-between items-center">
          <h1 className="text-xl font-bold text-gray-900 flex items-center gap-2 tracking-tight">
            <BookOpen size={22} className="text-blue-600" />
            FrenchMemo
          </h1>
          <div className="text-xs text-gray-400 font-medium font-mono">
             {user ? `UID: ${user.uid.slice(0,4)}` : '...'}
          </div>
        </div>
        {/* Tabs */}
        <div className="flex max-w-xl mx-auto px-6">
          <button onClick={() => setActiveTab('search')} className={`flex-1 pb-3 text-sm font-semibold border-b-2 transition-all ${activeTab === 'search' ? 'border-blue-600 text-blue-600' : 'border-transparent text-gray-400 hover:text-gray-600'}`}>Search</button>
          <button onClick={() => setActiveTab('favorites')} className={`flex-1 pb-3 text-sm font-semibold border-b-2 transition-all ${activeTab === 'favorites' ? 'border-blue-600 text-blue-600' : 'border-transparent text-gray-400 hover:text-gray-600'}`}>Favorites <span className="ml-1 px-1.5 py-0.5 bg-gray-100 text-gray-500 rounded-full text-xs">{favorites.length}</span></button>
        </div>
      </div>

      <div className="max-w-xl mx-auto px-6 py-8">
        {activeTab === 'search' && (
          <>
            <form onSubmit={handleSearch} className="relative mb-10 group z-10">
              <div className={`absolute inset-0 bg-blue-500/5 rounded-full blur-xl transition-opacity duration-300 ${loading ? 'opacity-100' : 'opacity-0 group-hover:opacity-100'}`}></div>
              <input type="text" value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} placeholder="Tapez un mot franÃ§ais..." className="w-full pl-14 pr-14 py-4 bg-white border-0 rounded-full shadow-[0_8px_30px_rgb(0,0,0,0.06)] text-xl font-medium placeholder:text-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500/20 transition-all relative z-10" />
              <Search className="absolute left-5 top-1/2 -translate-y-1/2 text-gray-400 z-20" size={22} />
              <button type="submit" disabled={loading} className="absolute right-3 top-1/2 -translate-y-1/2 p-2.5 bg-blue-600 text-white rounded-full hover:bg-blue-700 transition-colors disabled:opacity-70 hover:scale-105 active:scale-95 z-20 shadow-md">
                {loading ? <Loader2 className="animate-spin" size={20} /> : <ArrowRight size={20} />}
              </button>
            </form>
            {currentWordData && <WordCard data={currentWordData} />}
            {!currentWordData && !loading && (
              <div className="text-center py-16">
                <div className="bg-white p-6 rounded-full inline-block shadow-sm mb-6"><Search size={40} className="text-blue-100" /></div>
                <h3 className="text-gray-900 font-bold text-lg mb-2">PrÃªt Ã  apprendre ?</h3>
                <p className="text-gray-500 mb-8">Tapez un mot ci-dessus ou essayez ces suggestions.</p>
                <div className="flex flex-wrap justify-center gap-3">
                  {['Pomme', 'Montagne', 'RÃªve', 'LumiÃ¨re', 'OcÃ©an'].map(s => (
                    <button key={s} onClick={() => { setSearchTerm(s); handleSearch({preventDefault: () => {}}); }} className="px-5 py-2 bg-white shadow-sm hover:shadow-md border border-gray-100 rounded-full text-sm font-medium text-gray-600 hover:text-blue-600 transition-all hover:-translate-y-0.5">{s}</button>
                  ))}
                </div>
              </div>
            )}
          </>
        )}
        {activeTab === 'favorites' && (
          <div className="space-y-8">
            {favorites.length === 0 ? (
              <div className="text-center py-20"><div className="bg-gray-100 p-6 rounded-full inline-block mb-6"><Star size={40} className="text-gray-300" /></div><p className="text-gray-500 font-medium">Votre liste de favoris est vide.</p></div>
            ) : (
              favorites.map(fav => <WordCard key={fav.id} data={fav} isFavoriteView={true} onDelete={removeFromFavorites} />)
            )}
          </div>
        )}
      </div>
    </div>
  );
