import React, { useState, useEffect } from 'react';
import { Search, Volume2, Star, BookOpen, ArrowRight, Loader2, Tag, Layers, RefreshCcw } from 'lucide-react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInAnonymously, onAuthStateChanged } from 'firebase/auth';
import { getFirestore, collection, addDoc, query, onSnapshot, deleteDoc, doc, orderBy, serverTimestamp } from 'firebase/firestore';

// ==========================================
// ⚠️ 请在下方填入您的配置信息
// ==========================================

// 1. 填入您的 Firebase 配置信息 (从 Firebase 控制台 -> Project Settings -> General -> Your apps 复制)
const firebaseConfig = {
  apiKey: "YOUR_FIREBASE_API_KEY",
  authDomain: "your-project.firebaseapp.com",
  projectId: "your-project-id",
  storageBucket: "your-project.appspot.com",
  messagingSenderId: "123456789",
  appId: "1:123456:web:abcdef"
};

// 2. 填入您的 Gemini API Key
const GEMINI_API_KEY = "YOUR_GEMINI_API_KEY_HERE"; 

// ==========================================

// 初始化 Firebase
const app = initializeApp(firebaseConfig);
const auth = getAuth(app);
const db = getFirestore(app);
const appId = "french-memo-app"; // 本地数据库路径标识

// --- API Helper for Gemini ---
const fetchWordData = async (word) => {
  const apiKey = GEMINI_API_KEY; 

  if (!apiKey || apiKey.includes("YOUR_GEMINI_API_KEY")) {
    alert("请在代码中设置您的 Gemini API Key");
    throw new Error("API Key not set");
  }

  const prompt = `
    Analyze the French word "${word}". 
    Return strictly valid JSON (no markdown formatting) with this schema:
    {
      "word": "${word}",
      "definitions": {
        "en": "English definition",
        "zh": "Chinese definition (Simplified)"
      },
      "partOfSpeech": "noun/verb/adj etc",
      "collection": "Semantic category (e.g., Fruit, Animal, Emotion, Furniture)",
      "relatedWords": ["synonym1", "synonym2", "antonym1"],
      "examplePhrase": {
        "fr": "A simple French sentence using the word",
        "en": "English translation of the sentence"
      }
    }
  `;

  try {
    const response = await fetch(
      `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-09-2025:generateContent?key=${apiKey}`,
      {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          contents: [{ parts: [{ text: prompt }] }],
          generationConfig: { responseMimeType: "application/json" }
        }),
      }
    );

    const data = await response.json();
    const text = data.candidates?.[0]?.content?.parts?.[0]?.text;
    return text ? JSON.parse(text) : null;
  } catch (error) {
    console.error("Gemini API Error:", error);
    throw new Error("Failed to fetch word data.");
  }
};

export default function App() {
  const [user, setUser] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(false);
  const [currentWordData, setCurrentWordData] = useState(null);
  const [favorites, setFavorites] = useState([]);
  const [activeTab, setActiveTab] = useState('search'); // 'search' or 'favorites'

  // 生成图片 URL
  const getImageUrl = (keyword) => `https://image.pollinations.ai/prompt/realistic%20${keyword}%20french%20context?width=600&height=400&nologo=true`;

  // --- Auth & Data Sync ---
  useEffect(() => {
    const initAuth = async () => {
      try {
        await signInAnonymously(auth);
      } catch (error) {
        console.error("Auth Error:", error);
      }
    };
    initAuth();
    const unsubscribe = onAuthStateChanged(auth, setUser);
    return () => unsubscribe();
  }, []);

  useEffect(() => {
    if (!user) return;
    const q = query(collection(db, 'artifacts', appId, 'users', user.uid, 'favorites'));

    // 实时同步收藏夹
    const unsubscribe = onSnapshot(q, 
      (snapshot) => {
        const favs = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        // 在内存中排序以避免复杂的 Firestore 索引要求
        favs.sort((a, b) => (b.timestamp?.seconds || 0) - (a.timestamp?.seconds || 0));
        setFavorites(favs);
      },
      (error) => console.error("Firestore sync error:", error)
    );
    return () => unsubscribe();
  }, [user]);

  // --- Core Actions ---

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchTerm.trim()) return;

    setLoading(true);
    setCurrentWordData(null);
    setActiveTab('search');

    try {
      const data = await fetchWordData(searchTerm);
      setCurrentWordData(data);
    } catch (err) {
      alert("Could not find word. Please check your API key and try again.");
    } finally {
      setLoading(false);
    }
  };

  const handleSpeak = (text) => {
    if ('speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.lang = 'fr-FR';
      utterance.rate = 0.9;
      window.speechSynthesis.speak(utterance);
    } else {
      alert("Browser does not support Text-to-Speech");
    }
  };

  const addToFavorites = async () => {
    if (!user || !currentWordData) return;
    try {
      await addDoc(collection(db, 'artifacts', appId, 'users', user.uid, 'favorites'), {
        ...currentWordData,
        timestamp: serverTimestamp()
      });
    } catch (err) {
      console.error("Error adding favorite:", err);
    }
  };

  const removeFromFavorites = async (id) => {
    if (!user) return;
    try {
      await deleteDoc(doc(db, 'artifacts', appId, 'users', user.uid, 'favorites', id));
    } catch (err) {
      console.error("Error removing favorite:", err);
    }
  };

  const searchRelated = (word) => {
    setSearchTerm(word);
    setLoading(true);
    setCurrentWordData(null);
    fetchWordData(word).then(data => {
      setCurrentWordData(data);
      setLoading(false);
    });
  };

  // --- UI Components ---

  const WordCard = ({ data, isFavoriteView = false, onDelete }) => (
    <div className="bg-white rounded-xl shadow-lg overflow-hidden border border-slate-100 mb-6 transition-all hover:shadow-xl">
      <div className="h-48 overflow-hidden relative bg-slate-100">
        <img 
          src={getImageUrl(data.word)} 
          alt={data.word}
          className="w-full h-full object-cover transition-transform duration-700 hover:scale-105"
          onError={(e) => e.target.style.display = 'none'} 
        />
        <div className="absolute top-4 right-4 flex gap-2">
          <span className="bg-white/90 backdrop-blur-sm px-3 py-1 rounded-full text-xs font-bold text-indigo-600 uppercase tracking-wider shadow-sm">
            {data.partOfSpeech}
          </span>
          <span className="bg-indigo-600/90 backdrop-blur-sm px-3 py-1 rounded-full text-xs font-bold text-white shadow-sm flex items-center gap-1">
            <Layers size={12} />
            {data.collection}
          </span>
        </div>
      </div>

      <div className="p-6">
        <div className="flex justify-between items-start mb-4">
          <div>
            <h2 className="text-4xl font-bold text-slate-800 mb-1 flex items-center gap-3">
              {data.word}
              <button 
                onClick={() => handleSpeak(data.word)}
                className="p-2 rounded-full bg-indigo-50 text-indigo-600 hover:bg-indigo-100 transition-colors"
                title="Pronounce"
              >
                <Volume2 size={24} />
              </button>
            </h2>
          </div>
          {!isFavoriteView && (
            <button 
              onClick={addToFavorites}
              className="p-2 rounded-full text-slate-400 hover:text-yellow-500 hover:bg-yellow-50 transition-colors"
              title="Add to Favorites"
            >
              <Star size={24} />
            </button>
          )}
          {isFavoriteView && (
            <button 
              onClick={() => onDelete(data.id)}
              className="p-2 rounded-full text-red-400 hover:text-red-600 hover:bg-red-50 transition-colors"
              title="Remove"
            >
              <div className="font-bold text-xl leading-none">&times;</div>
            </button>
          )}
        </div>

        <div className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="bg-slate-50 p-3 rounded-lg border border-slate-100">
              <span className="text-xs font-semibold text-slate-400 uppercase block mb-1">English</span>
              <p className="text-slate-700 font-medium">{data.definitions.en}</p>
            </div>
            <div className="bg-slate-50 p-3 rounded-lg border border-slate-100">
              <span className="text-xs font-semibold text-slate-400 uppercase block mb-1">Chinese</span>
              <p className="text-slate-700 font-medium">{data.definitions.zh}</p>
            </div>
          </div>

          <div className="bg-indigo-50 p-4 rounded-lg border border-indigo-100">
            <div className="flex items-start gap-3">
              <BookOpen className="text-indigo-500 mt-1 shrink-0" size={18} />
              <div>
                <p className="text-indigo-900 font-medium italic">"{data.examplePhrase.fr}"</p>
                <p className="text-indigo-600/80 text-sm mt-1">{data.examplePhrase.en}</p>
              </div>
            </div>
          </div>

          {!isFavoriteView && data.relatedWords && data.relatedWords.length > 0 && (
            <div>
              <span className="text-xs font-semibold text-slate-400 uppercase block mb-2">Related Words</span>
              <div className="flex flex-wrap gap-2">
                {data.relatedWords.map((word, idx) => (
                  <button 
                    key={idx}
                    onClick={() => searchRelated(word)}
                    className="px-3 py-1 bg-white border border-slate-200 rounded-full text-sm text-slate-600 hover:border-indigo-300 hover:text-indigo-600 transition-colors flex items-center gap-1"
                  >
                    {word} <ArrowRight size={12} />
                  </button>
                ))}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-slate-50 font-sans text-slate-900 pb-20">
      {/* Header */}
      <div className="bg-white border-b border-slate-200 sticky top-0 z-10">
        <div className="max-w-md mx-auto px-4 py-4 flex justify-between items-center">
          <h1 className="text-xl font-bold bg-gradient-to-r from-indigo-600 to-violet-600 bg-clip-text text-transparent flex items-center gap-2">
            <BookOpen size={24} className="text-indigo-600" />
            FrenchMemo
          </h1>
          <div className="text-xs text-slate-400 font-medium px-2 py-1 bg-slate-100 rounded-md">
             {user ? `User: ${user.uid.slice(0,4)}...` : 'Connecting...'}
          </div>
        </div>

        {/* Navigation Tabs */}
        <div className="flex max-w-md mx-auto px-4">
          <button 
            onClick={() => setActiveTab('search')}
            className={`flex-1 pb-3 text-sm font-medium border-b-2 transition-colors ${activeTab === 'search' ? 'border-indigo-600 text-indigo-600' : 'border-transparent text-slate-500'}`}
          >
            Search
          </button>
          <button 
            onClick={() => setActiveTab('favorites')}
            className={`flex-1 pb-3 text-sm font-medium border-b-2 transition-colors ${activeTab === 'favorites' ? 'border-indigo-600 text-indigo-600' : 'border-transparent text-slate-500'}`}
          >
            Favorites ({favorites.length})
          </button>
        </div>
      </div>

      <div className="max-w-md mx-auto px-4 py-6">
        {activeTab === 'search' && (
          <>
            <form onSubmit={handleSearch} className="relative mb-8">
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Search a word (e.g. Pomme)..."
                className="w-full pl-12 pr-4 py-4 bg-white border border-slate-200 rounded-2xl shadow-sm text-lg focus:outline-none focus:ring-2 focus:ring-indigo-500/20 focus:border-indigo-500 transition-all"
              />
              <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400" size={20} />
              <button 
                type="submit"
                disabled={loading}
                className="absolute right-3 top-1/2 -translate-y-1/2 p-2 bg-indigo-600 text-white rounded-xl hover:bg-indigo-700 transition-colors disabled:opacity-50"
              >
                {loading ? <Loader2 className="animate-spin" size={20} /> : <ArrowRight size={20} />}
              </button>
            </form>

            {currentWordData && <WordCard data={currentWordData} />}

            {!currentWordData && !loading && (
              <div className="text-center py-12 text-slate-400">
                <Search size={48} className="mx-auto mb-4 opacity-20" />
                <p>Enter a French word to start learning.</p>
                <div className="mt-6 flex flex-wrap justify-center gap-2">
                  {['Pomme', 'Chat', 'Heureux', 'Maison', 'Voyage'].map(suggestion => (
                    <button 
                      key={suggestion}
                      onClick={() => { setSearchTerm(suggestion); handleSearch({preventDefault: () => {}}); }} 
                      className="px-4 py-2 bg-white border border-slate-200 rounded-full text-sm text-slate-600 hover:border-indigo-300 hover:text-indigo-600"
                    >
                      {suggestion}
                    </button>
                  ))}
                </div>
              </div>
            )}
          </>
        )}

        {activeTab === 'favorites' && (
          <div className="space-y-6">
            {favorites.length === 0 ? (
              <div className="text-center py-12 text-slate-400">
                <Star size={48} className="mx-auto mb-4 opacity-20" />
                <p>No favorites yet. Search and add some!</p>
              </div>
            ) : (
              favorites.map(fav => (
                <WordCard 
                  key={fav.id} 
                  data={fav} 
                  isFavoriteView={true} 
                  onDelete={removeFromFavorites}
                />
              ))
            )}
          </div>
        )}
      </div>
    </div>
  );
}
