import React, { useState, useEffect } from 'react';
import { Search, Volume2, Star, BookOpen, ArrowRight, Loader2, Tag, Layers, X } from 'lucide-react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInAnonymously, onAuthStateChanged } from 'firebase/auth';
import { getFirestore, collection, addDoc, query, onSnapshot, deleteDoc, doc, orderBy, serverTimestamp } from 'firebase/firestore';

// ==========================================
// ⚠️ 请在下方填入您的配置信息
// ==========================================

// 1. 填入您的 Firebase 配置信息 (从 Firebase 控制台 -> Project Settings -> General -> Your apps 复制)
const firebaseConfig = {
  apiKey: "YOUR_FIREBASE_API_KEY",
  authDomain: "your-project.firebaseapp.com",
  projectId: "your-project-id",
  storageBucket: "your-project.appspot.com",
  messagingSenderId: "123456789",
  appId: "1:123456:web:abcdef"
};

// 2. 填入您的 Gemini API Key
const GEMINI_API_KEY = "YOUR_GEMINI_API_KEY_HERE"; 

// ==========================================

// 初始化 Firebase
const app = initializeApp(firebaseConfig);
const auth = getAuth(app);
const db = getFirestore(app);
const appId = "french-memo-app"; // 本地数据库路径标识

// --- API Helper for Gemini ---
const fetchWordData = async (word) => {
  const apiKey = GEMINI_API_KEY; 

  if (!apiKey || apiKey.includes("YOUR_GEMINI_API_KEY")) {
    alert("请在代码中设置您的 Gemini API Key");
    throw new Error("API Key not set");
  }

  const prompt = `
    Analyze the French word "${word}". 
    Return strictly valid JSON (no markdown formatting) with this schema:
    {
      "word": "${word}",
      "definitions": {
        "en": "English definition",
        "zh": "Chinese definition (Simplified)"
      },
      "partOfSpeech": "noun/verb/adj etc",
      "collection": "Semantic category (e.g., Fruit, Animal, Emotion, Furniture)",
      "relatedWords": ["synonym1", "synonym2", "antonym1"],
      "examplePhrase": {
        "fr": "A simple French sentence using the word",
        "en": "English translation of the sentence"
      }
    }
  `;

  try {
    const response = await fetch(
      `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-09-2025:generateContent?key=${apiKey}`,
      {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          contents: [{ parts: [{ text: prompt }] }],
          generationConfig: { responseMimeType: "application/json" }
        }),
      }
    );

    const data = await response.json();
    const text = data.candidates?.[0]?.content?.parts?.[0]?.text;
    return text ? JSON.parse(text) : null;
  } catch (error) {
    console.error("Gemini API Error:", error);
    throw new Error("Failed to fetch word data.");
  }
};

export default function App() {
  const [user, setUser] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(false);
  const [currentWordData, setCurrentWordData] = useState(null);
  const [favorites, setFavorites] = useState([]);
  const [activeTab, setActiveTab] = useState('search'); // 'search' or 'favorites'

  // 生成图片 URL
  const getImageUrl = (keyword) => `https://image.pollinations.ai/prompt/realistic%20${keyword}%20french%20context?width=600&height=400&nologo=true`;

  // --- Auth & Data Sync ---
  useEffect(() => {
    const initAuth = async () => {
      try {
        await signInAnonymously(auth);
      } catch (error) {
        console.error("Auth Error:", error);
      }
    };
    initAuth();
    const unsubscribe = onAuthStateChanged(auth, setUser);
    return () => unsubscribe();
  }, []);

  useEffect(() => {
    if (!user) return;
    const q = query(collection(db, 'artifacts', appId, 'users', user.uid, 'favorites'));

    const unsubscribe = onSnapshot(q, 
      (snapshot) => {
        const favs = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        favs.sort((a, b) => (b.timestamp?.seconds || 0) - (a.timestamp?.seconds || 0));
        setFavorites(favs);
      },
      (error) => console.error("Firestore sync error:", error)
    );
    return () => unsubscribe();
  }, [user]);

  // --- Core Actions ---

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchTerm.trim()) return;

    setLoading(true);
    setCurrentWordData(null);
    setActiveTab('search');

    try {
      const data = await fetchWordData(searchTerm);
      setCurrentWordData(data);
    } catch (err) {
      alert("Could not find word. Please check your API key and try again.");
    } finally {
      setLoading(false);
    }
  };

  const handleSpeak = (text) => {
    if ('speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.lang = 'fr-FR';
      utterance.rate = 0.9;
      window.speechSynthesis.speak(utterance);
    } else {
      alert("Browser does not support Text-to-Speech");
    }
  };

  const addToFavorites = async () => {
    if (!user || !currentWordData) return;
    try {
      await addDoc(collection(db, 'artifacts', appId, 'users', user.uid, 'favorites'), {
        ...currentWordData,
        timestamp: serverTimestamp()
      });
    } catch (err) {
      console.error("Error adding favorite:", err);
    }
  };

  const removeFromFavorites = async (id) => {
    if (!user) return;
    try {
      await deleteDoc(doc(db, 'artifacts', appId, 'users', user.uid, 'favorites', id));
    } catch (err) {
      console.error("Error removing favorite:", err);
    }
  };

  const searchRelated = (word) => {
    setSearchTerm(word);
    setLoading(true);
    setCurrentWordData(null);
    fetchWordData(word).then(data => {
      setCurrentWordData(data);
      setLoading(false);
    });
  };

  // --- UI Components ---

  const WordCard = ({ data, isFavoriteView = false, onDelete }) => (
    <div className="bg-white rounded-2xl shadow-sm hover:shadow-xl transition-shadow duration-300 overflow-hidden border border-gray-100 mb-8">
      {/* Image Header */}
      <div className="h-44 overflow-hidden relative bg-gray-50">
        <img 
          src={getImageUrl(data.word)} 
          alt={data.word}
          className="w-full h-full object-cover opacity-90"
          onError={(e) => e.target.style.display = 'none'} 
        />
        <div className="absolute top-4 right-4 flex gap-2">
          <span className="bg-white/80 backdrop-blur-md px-3 py-1 rounded-full text-xs font-bold text-blue-600 uppercase tracking-wider shadow-sm">
            {data.partOfSpeech}
          </span>
          <span className="bg-blue-600/90 backdrop-blur-md px-3 py-1 rounded-full text-xs font-bold text-white shadow-sm flex items-center gap-1">
            <Layers size={12} />
            {data.collection}
          </span>
        </div>
      </div>

      {/* Card Content */}
      <div className="p-8">
        {/* Word & Actions Row */}
        <div className="flex justify-between items-start mb-8">
          <div className="flex items-center gap-4">
            <h2 className="text-5xl font-bold text-gray-900 tracking-tight">
              {data.word}
            </h2>
            <button 
              onClick={() => handleSpeak(data.word)}
              className="p-3 rounded-full bg-blue-50 text-blue-600 hover:bg-blue-100 transition-colors"
              title="Pronounce"
            >
              <Volume2 size={24} />
            </button>
          </div>
          {!isFavoriteView && (
            <button 
              onClick={addToFavorites}
              className="p-3 rounded-full text-gray-400 hover:text-yellow-500 hover:bg-yellow-50 transition-colors"
              title="Add to Favorites"
            >
              <Star size={24} />
            </button>
          )}
          {isFavoriteView && (
            <button 
              onClick={() => onDelete(data.id)}
              className="p-3 rounded-full text-gray-300 hover:text-red-500 hover:bg-red-50 transition-colors group"
              title="Remove"
            >
              <X size={24} className="group-hover:scale-110 transition-transform" />
            </button>
          )}
        </div>

        <div className="space-y-8">
          {/* Definitions - Minimalist text blocks */}
          <div className="space-y-6">
            <div>
              <h3 className="text-xs font-semibold text-gray-400 uppercase tracking-wider mb-2">English</h3>
              <p className="text-xl text-gray-800 leading-relaxed font-medium">{data.definitions.en}</p>
            </div>
            <div>
              <h3 className="text-xs font-semibold text-gray-400 uppercase tracking-wider mb-2">Chinese</h3>
              <p className="text-xl text-gray-800 leading-relaxed font-medium">{data.definitions.zh}</p>
            </div>
          </div>

          {/* Example Phrase - Minimal quote style */}
          <div className="pl-4 border-l-4 border-blue-200 italic py-2 my-8">
            <p className="text-2xl text-gray-700 font-serif leading-snug">"{data.examplePhrase.fr}"</p>
            <p className="text-gray-500 text-sm mt-3 not-italic flex items-center gap-2">
              <ArrowRight size={14} className="text-blue-300" /> {data.examplePhrase.en}
            </p>
          </div>

          {/* Related Words - Cleaner tags */}
          {!isFavoriteView && data.relatedWords && data.relatedWords.length > 0 && (
            <div className="pt-4 border-t border-gray-100">
              <span className="text-xs font-semibold text-gray-400 uppercase block mb-3">Related Words</span>
              <div className="flex flex-wrap gap-2">
                {data.relatedWords.map((word, idx) => (
                  <button 
                    key={idx}
                    onClick={() => searchRelated(word)}
                    className="px-4 py-1.5 bg-gray-50 hover:bg-blue-50 border border-gray-200 hover:border-blue-200 rounded-full text-sm font-medium text-gray-600 hover:text-blue-600 transition-all flex items-center gap-1"
                  >
                    {word}
                  </button>
                ))}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-50 font-sans text-gray-900 pb-20">
      {/* Header - Clean & Minimal */}
      <div className="bg-white/80 backdrop-blur-md border-b border-gray-100 sticky top-0 z-20">
        <div className="max-w-xl mx-auto px-6 py-4 flex justify-between items-center">
          <h1 className="text-xl font-bold text-gray-900 flex items-center gap-2 tracking-tight">
            <BookOpen size={22} className="text-blue-600" />
            FrenchMemo
          </h1>
          <div className="text-xs text-gray-400 font-medium font-mono">
             {user ? `UID: ${user.uid.slice(0,4)}` : '...'}
          </div>
        </div>

        {/* Navigation Tabs - Refined */}
        <div className="flex max-w-xl mx-auto px-6">
          <button 
            onClick={() => setActiveTab('search')}
            className={`flex-1 pb-3 text-sm font-semibold border-b-2 transition-all ${activeTab === 'search' ? 'border-blue-600 text-blue-600' : 'border-transparent text-gray-400 hover:text-gray-600'}`}
          >
            Search
          </button>
          <button 
            onClick={() => setActiveTab('favorites')}
            className={`flex-1 pb-3 text-sm font-semibold border-b-2 transition-all ${activeTab === 'favorites' ? 'border-blue-600 text-blue-600' : 'border-transparent text-gray-400 hover:text-gray-600'}`}
          >
            Favorites <span className="ml-1 px-1.5 py-0.5 bg-gray-100 text-gray-500 rounded-full text-xs">{favorites.length}</span>
          </button>
        </div>
      </div>

      <div className="max-w-xl mx-auto px-6 py-8">
        {activeTab === 'search' && (
          <>
            {/* Search Bar - Floating & Rounded */}
            <form onSubmit={handleSearch} className="relative mb-10 group z-10">
              <div className={`absolute inset-0 bg-blue-500/5 rounded-full blur-xl transition-opacity duration-300 ${loading ? 'opacity-100' : 'opacity-0 group-hover:opacity-100'}`}></div>
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Type a French word..."
                className="w-full pl-14 pr-14 py-4 bg-white border-0 rounded-full shadow-[0_8px_30px_rgb(0,0,0,0.06)] text-xl font-medium placeholder:text-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500/20 transition-all relative z-10"
              />
              <Search className="absolute left-5 top-1/2 -translate-y-1/2 text-gray-400 z-20" size={22} />
              <button 
                type="submit"
                disabled={loading}
                className="absolute right-3 top-1/2 -translate-y-1/2 p-2.5 bg-blue-600 text-white rounded-full hover:bg-blue-700 transition-colors disabled:opacity-70 hover:scale-105 active:scale-95 z-20 shadow-md"
              >
                {loading ? <Loader2 className="animate-spin" size={20} /> : <ArrowRight size={20} />}
              </button>
            </form>

            {currentWordData && <WordCard data={currentWordData} />}

            {/* Empty State - Clean */}
            {!currentWordData && !loading && (
              <div className="text-center py-16">
                <div className="bg-white p-6 rounded-full inline-block shadow-sm mb-6">
                    <Search size={40} className="text-blue-100" />
                </div>
                <h3 className="text-gray-900 font-bold text-lg mb-2">Ready to learn?</h3>
                <p className="text-gray-500 mb-8">Type a word above or try one of these suggestions.</p>
                <div className="flex flex-wrap justify-center gap-3">
                  {['Pomme', 'Montagne', 'Rêve', 'Lumière', 'Océan'].map(suggestion => (
                    <button 
                      key={suggestion}
                      onClick={() => { setSearchTerm(suggestion); handleSearch({preventDefault: () => {}}); }} 
                      className="px-5 py-2 bg-white shadow-sm hover:shadow-md border border-gray-100 rounded-full text-sm font-medium text-gray-600 hover:text-blue-600 transition-all hover:-translate-y-0.5"
                    >
                      {suggestion}
                    </button>
                  ))}
                </div>
              </div>
            )}
          </>
        )}

        {activeTab === 'favorites' && (
          <div className="space-y-8">
            {favorites.length === 0 ? (
              <div className="text-center py-20">
                 <div className="bg-gray-100 p-6 rounded-full inline-block mb-6">
                    <Star size={40} className="text-gray-300" />
                </div>
                <p className="text-gray-500 font-medium">Your favorites list is empty.</p>
              </div>
            ) : (
              favorites.map(fav => (
                <WordCard 
                  key={fav.id} 
                  data={fav} 
                  isFavoriteView={true} 
                  onDelete={removeFromFavorites}
                />
              ))
            )}
          </div>
        )}
      </div>
    </div>
  );
}
